import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { X, Minimize2, Send, Bot, User, ExternalLink } from 'lucide-react';
import { marked } from 'marked';
import { useLanguage } from '../hooks/useLanguage';
import { MatrixBackground } from './CyberpunkEffects';
import markusPhoto from '@assets/Markus_Maiwald_2iextb2iextb2iex_1756293542998.png';

interface ChatbotAppProps {
  isOpen: boolean;
  onClose: () => void;
}

interface Message {
  id: string;
  text: string;
  sender: 'user' | 'bot';
  timestamp: Date;
}

// Configure marked for safe HTML parsing
marked.setOptions({
  breaks: true,
  gfm: true,
});

// Function to parse markdown and create structured content
const parseMarkdownToStructuredContent = (text: string) => {
  try {
    // Convert markdown to HTML - marked.parse is synchronous
    const html = marked.parse(text);

    // Parse HTML and create structured JSX
    const parser = new DOMParser();
    const doc = parser.parseFromString(html, 'text/html');

    const elements: React.ReactNode[] = [];
    let elementKey = 0;

    const processNode = (node: Node): React.ReactNode => {
      if (node.nodeType === Node.TEXT_NODE) {
        return node.textContent;
      }

      if (node.nodeType === Node.ELEMENT_NODE) {
        const element = node as Element;
        const children = Array.from(element.childNodes).map(child => processNode(child)).filter(Boolean);

        switch (element.tagName.toLowerCase()) {
          case 'h1':
          case 'h2':
          case 'h3':
            return <h3 key={elementKey++} className="text-cyan-200 font-bold text-base mb-2 mt-4 first:mt-0">{children}</h3>;
          case 'p':
            return <p key={elementKey++} className="mb-3 leading-relaxed last:mb-0">{children}</p>;
          case 'strong':
            return <strong key={elementKey++} className="text-cyan-200 font-bold">{children}</strong>;
          case 'em':
            return <em key={elementKey++} className="text-cyan-300 italic">{children}</em>;
          case 'ul':
            return <ul key={elementKey++} className="mb-3">{children}</ul>;
          case 'li':
            return (
              <div key={elementKey++} className="flex items-start mb-1 ml-2">
                <span className="text-cyan-400 mr-2 flex-shrink-0">â€¢</span>
                <span className="leading-relaxed">{children}</span>
              </div>
            );
          case 'code':
            return <code key={elementKey++} className="bg-gray-800 text-cyan-300 px-1 rounded text-sm">{children}</code>;
          default:
            return <span key={elementKey++}>{children}</span>;
        }
      }

      return null;
    };

    Array.from(doc.body.childNodes).forEach(node => {
      const processed = processNode(node);
      if (processed) elements.push(processed);
    });

    return elements;
  } catch (error) {
    console.error('Markdown parsing error:', error);
    // Fallback to plain text parsing
    return text.split('\n\n').map((paragraph, index) => (
      <p key={index} className="mb-3 leading-relaxed last:mb-0">
        {paragraph.trim()}
      </p>
    ));
  }
};

export const ChatbotApp: React.FC<ChatbotAppProps> = ({ isOpen, onClose }) => {
  const { currentLanguage } = useLanguage();
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [messages, setMessages] = useState<Message[]>([
    {
      id: '1',
      text: currentLanguage === 'EN' 
        ? 'You\'ve accessed the public-facing instance of Markus Maiwald\'s strategic AI system. I serve as his digital attachÃ©, demonstrating the capabilities of our architected partnership. I can discuss his work, philosophies, and the nature of strategic AI collaboration. How may I provide a demonstration of these capabilities?'
        : 'Sie haben auf die Ã¶ffentliche Instanz von Markus Maiwalds strategischem KI-System zugegriffen. Ich diene als sein digitaler AttachÃ© und demonstriere die FÃ¤higkeiten unserer architektonierten Partnerschaft. Ich kann seine Arbeit, Philosophien und die Natur strategischer KI-Zusammenarbeit diskutieren. Wie kann ich eine Demonstration dieser FÃ¤higkeiten bereitstellen?',
      sender: 'bot',
      timestamp: new Date()
    }
  ]);
  const [inputText, setInputText] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  // Mouse tracking for custom cursor
  useEffect(() => {
    const handleMouseMove = (e: MouseEvent) => {
      setMousePosition({ x: e.clientX, y: e.clientY });
    };

    if (isOpen) {
      document.addEventListener('mousemove', handleMouseMove);
    }

    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
    };
  }, [isOpen]);

  // Escape key handler
  useEffect(() => {
    const handleEscape = (e: KeyboardEvent) => {
      if (e.key === 'Escape' && isOpen) {
        onClose();
      }
    };

    document.addEventListener('keydown', handleEscape);
    return () => document.removeEventListener('keydown', handleEscape);
  }, [isOpen, onClose]);

  // Auto-scroll to bottom
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSendMessage = async () => {
    if (!inputText.trim()) return;

    const userMessage: Message = {
      id: Date.now().toString(),
      text: inputText,
      sender: 'user',
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    const messageText = inputText;
    setInputText('');
    setIsTyping(true);

    try {
      // Call actual AI API
      const response = await fetch('/api/ai/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ message: messageText }),
      });

      if (!response.ok) {
        throw new Error('Failed to get AI response');
      }

      const data = await response.json();

      const botResponse: Message = {
        id: (Date.now() + 1).toString(),
        text: data.response || 'I apologize, but I was unable to generate a response.',
        sender: 'bot',
        timestamp: new Date()
      };
      setMessages(prev => [...prev, botResponse]);
    } catch (error) {
      console.error('AI Chat error:', error);
      const errorResponse: Message = {
        id: (Date.now() + 1).toString(),
        text: currentLanguage === 'EN'
          ? 'I apologize, but I encountered an error. Please try again or visit http://chat.maiwald.work for the full experience.'
          : 'Entschuldigung, aber es ist ein Fehler aufgetreten. Bitte versuchen Sie es erneut oder besuchen Sie http://chat.maiwald.work fÃ¼r die vollstÃ¤ndige Erfahrung.',
        sender: 'bot',
        timestamp: new Date()
      };
      setMessages(prev => [...prev, errorResponse]);
    } finally {
      setIsTyping(false);
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  if (!isOpen) return null;

  return (
    <AnimatePresence>
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
        className="fixed inset-0 bg-black/80 backdrop-blur-sm z-50 flex items-center justify-center p-4"
        style={{ cursor: 'none' }}
      >
        {/* Matrix Background */}
