      'STATUS: ACTIVE | PRIORITY: MAXIMUM'
    ],
    'run ./manifest.yaml': [
      'Executing manifest.yaml...',
      '',
      'apiVersion: maiwald.enterprises/v1',
      'kind: StrategicManifesto',
      'metadata:',
      '  name: maiwald-enterprise-philosophy',
      '  namespace: strategic-architecture',
      'spec:',
