import { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import { CyberpunkAudio } from './CyberpunkEffects';

interface ScrollProgressProps {
  sections: string[];
  currentSection: number;
  onSectionChange: (index: number) => void;
}

export function ScrollProgress({ sections, currentSection, onSectionChange }: ScrollProgressProps) {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => setIsVisible(true), 2000);
    return () => clearTimeout(timer);
  }, []);

  if (!isVisible) return null;

  return (
    <motion.div
      initial={{ opacity: 0, x: 50 }}
      animate={{ opacity: 0.5, x: 0 }}
      whileHover={{ opacity: 1 }}
      transition={{ duration: 0.3 }}
      className="fixed right-8 top-1/2 transform -translate-y-1/2 z-40 space-y-4 bg-black bg-opacity-30 backdrop-blur-sm rounded-lg p-4 border border-cyberpunk-border hover:bg-opacity-50"
    >
      {sections.map((section, index) => (
        <motion.div
          key={section}
          className="flex items-center justify-end space-x-3 cursor-pointer group"
          onMouseEnter={() => CyberpunkAudio.playHoverClick()}
          onClick={() => {
            CyberpunkAudio.playButtonClick();
            onSectionChange(index);
          }}
          whileHover={{ scale: 1.1 }}
        >
          <motion.span
            className={`text-sm font-mono font-bold transition-all duration-300 text-right ${
              index === currentSection
                ? 'text-cyberpunk-electric-blue opacity-100 text-glow'
                : 'text-white opacity-90 group-hover:opacity-100 group-hover:text-cyberpunk-electric-blue'
            }`}
            style={{
              textShadow: index === currentSection 
                ? '0 0 10px var(--cyberpunk-electric-blue)' 
                : '0 0 5px rgba(255, 255, 255, 0.5)'
            }}
            initial={{ opacity: 0, x: 10 }}
            animate={{ 
              opacity: index === currentSection ? 1 : 0.9,
              x: index === currentSection ? 0 : 10 
            }}
          >
            {section}
          </motion.span>
          <motion.div
            className={`w-3 h-3 rounded-full border-2 transition-all duration-300 ${
              index === currentSection
                ? 'bg-cyberpunk-electric-blue border-cyberpunk-electric-blue shadow-lg shadow-cyberpunk-electric-blue'
                : 'border-cyberpunk-text-dim hover:border-cyberpunk-electric-blue'
            }`}
            animate={{
              scale: index === currentSection ? 1.2 : 1,
              boxShadow: index === currentSection 
                ? '0 0 20px var(--cyberpunk-electric-blue)' 
                : '0 0 0px transparent'
            }}
          />
        </motion.div>
      ))}
    </motion.div>
  );
}

