    return text.split('\n').map((line, index) => {
      if (line.startsWith('ðŸ§ ') || line.startsWith('â˜ï¸') || line.startsWith('ðŸ§±') || 
          line.startsWith('ðŸŒ') || line.startsWith('ðŸ”') || line.startsWith('ðŸ’¾') || 
